mapper.select:


select board_id, board_category, board_title, board_content, board_file, board_regist
from(
	select * from (
		select rownum as rn, board_id, user_id, board_title, board_date 
		from (
			select * 
			from board
			where user_id like '%'||#{search}||'%' or board_title like '%'||#{search}||'%'
			order by board_regist desc
		)
	)
	where rn &gt;= #{start} and rn &lt;= #{end}
),(
select board_id, board_file
from member
where user_id in (
	select user_id
	from (
		select rownum as rn, board_id, user_id, board_title, board_regist
		from (
			select*
			from board
			where user_id like '%'||#{search}||'%' or board_title like '%'||#{search}||'%'
			order by board_regist desc
			)
		)
		where rn &gt;= #{start} and rn &lt;= #{end}
	)


select count(*) 
from board
where user_id = #{user_id}
